import axios from 'axios';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import React, { Component } from 'react';


import '../../../../node_modules/react-tabs/style/react-tabs.css';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
        categories: [{
            id: null,
            name: null,
            displayName: null
        }],
        sent: [{
            id: null,
            categoryName: null,
            raveCount: null
        }],
        received: [{
            id: null,
            categoryName: null,
            raveCount: null
        }],
        sentRaves: [{
            id: null,
            coEmployees: [
                {
                    resId: null,
                    resName: null,
                    resNameMiddle: null,
                    resNameLast: null,
                    resDesignation: null,
                    resDepartment: null,
                    resProfileImage: null
                }
            ],
            categoryName: null,
            comment: null,
            sent: true,
            acknowledgedBy: [],
            timestamp: null,
            raveCount: null
        }],
        receivedRaves: [{
            id: null,
            coEmployees: [
                {
                    resId: null,
                    resName: null,
                    resNameMiddle: null,
                    resNameLast: null,
                    resDesignation: null,
                    resDepartment: null,
                    resProfileImage: null
                }
            ],
            categoryName: null,
            comment: null,
            sent: true,
            acknowledgedBy: [],
            timestamp: null,
            raveCount: null
        }],

        selectedCoEmpTags: [],
        suggesions: [],

        selectedCoEmpTagsWitness: [],
        suggesionsWitness: [],

        commentCounter: 0,

        acknowledgement: {
            ackId: null,
            raveId: null,
            ackBy: null,
            ackOn: null
        },

        sendRaveRequestCoEmps: [],
        sendRaveRequestCategory: null,
        sendRaveRequestWitness: [],
        sendRaveRequestComment: null,

        sendRaveResponse: []



    };

    //employee search methods for rave receivers
    this.getSuggesions = this.getSuggesions.bind(this);
    this.addToTags = this.addToTags.bind(this);
    this.deleteTags = this.deleteTags.bind(this);

    //set category method 
    this.setCategory = this.setCategory.bind(this);

    //employee search methods for Witness
    this.getSuggesionsWitness = this.getSuggesionsWitness.bind(this);
    this.addToTagsWitness = this.addToTagsWitness.bind(this);
    this.deleteTagsWitness = this.deleteTagsWitness.bind(this);


    //load Default raves 
    this.loadAllSentRaves = this.loadAllSentRaves.bind(this);
    this.loadAllReceivedRaves = this.loadAllReceivedRaves.bind(this);

    //rave render methods under category type
    this.getRavesByCategory = this.getRavesByCategory.bind(this);

    //to count character limit of comment
    this.commentCharCounter = this.commentCharCounter.bind(this);
    this.commentGetBackspace = this.commentGetBackspace.bind(this);

    //rave acknowledgement methods
    this.acknowledgeRave = this.acknowledgeRave.bind(this);
    this.deAcknowledgeRave = this.deAcknowledgeRave.bind(this);

    //send raves method (post)
    this.sendRave = this.sendRave.bind(this);


}

componentDidMount() {
    let loginUse = 1; // logged user

    //load all categories
    axios.get('http://localhost:8080/api/rave/categories')
        .then(res => {
            this.setState({
                categories: res.data
            })
        });

    //count of sent raves     
    axios.get('http://localhost:8080/api/employee/' + loginUse + '/rave/categories?sent=true')
        .then(res => {
            this.setState({
                sent: res.data
            })
        });

    //count of received raves     
    axios.get('http://localhost:8080/api/employee/' + loginUse + '/rave/categories?sent=false')
        .then(res => {
            this.setState({
                received: res.data
            })
        });

    //sent raves without particular category (Default)    
    axios.get('http://localhost:8080/api/employee/' + loginUse + '/rave?sent=true&offset=0&limit=10')
        .then(res => {
            this.setState({
                sentRaves: res.data
            })
        });

    //Received raves without particular category (Default)    
    axios.get('http://localhost:8080/api/employee/' + loginUse + '/rave?sent=false&offset=0&limit=10')
        .then(res => {
            this.setState({
                receivedRaves: res.data
            })
        });



}

//employee search methods - to get suggesions
getSuggesions() {
    let ee = document.getElementById("coEmpInputer").value;
    let countOfEE = ee.length;
    if (countOfEE >= 3) {
        axios.get("http://localhost:8080/api/employee?search=" + ee + "&excludeme=true&excluderm=false&offset=0&limit=10")
            .then(res =>
                this.setState({
                    suggesions: res.data
                })
            );
    } else if (countOfEE == 0) {
        this.setState({
            suggesions: []
        })
    }
}

//employee search methods - to add suggessions to tags
addToTags(ee) {
    let i = "not-found";
    this.state.selectedCoEmpTags.map(data => {
        if (data.id == ee.id) {
            i = "found";
        }
    })

    if (i == "not-found") {
        this.setState({
            selectedCoEmpTags: this.state.selectedCoEmpTags.concat(ee)
        });
    }

}

//employee search methods - to delete selected tags
deleteTags(ee) {
    let temp_selectedCoEmpTags = this.state.selectedCoEmpTags.filter(item => item.id !== ee);
    this.setState({
        selectedCoEmpTags: temp_selectedCoEmpTags
    });
}


//get Category method
setCategory(cateId) {
    this.setState({
        sendRaveRequestCategory: cateId
    });
}




//employee search methods of Witness - to get suggesions
getSuggesionsWitness() {
    let ee = document.getElementById("witnessInputer").value;
    let countOfEE = ee.length;
    if (countOfEE >= 3) {
        axios.get("http://localhost:8080/api/employee?search=" + ee + "&excludeme=true&excluderm=true&offset=0&limit=10")
            .then(res =>
                this.setState({
                    suggesionsWitness: res.data
                })
            );
    } else if (countOfEE == 0) {
        this.setState({
            suggesionsWitness: []
        })
    }
}

//employee search methods of Witness - to add suggessions to tags
addToTagsWitness(ee) {
    let i = "not-found";

    this.state.selectedCoEmpTagsWitness.map(data => {
        if (data.id == ee.id) {
            i = "found";
        }
    })

    if (i == "not-found") {
        this.setState({
            selectedCoEmpTagsWitness: this.state.selectedCoEmpTagsWitness.concat(ee)
        });
    }

}

//employee search methods of Witness - to delete selected tags
deleteTagsWitness(ee) {
    let temp_selectedCoEmpTagsWitness = this.state.selectedCoEmpTagsWitness.filter(item => item.id !== ee);
    this.setState({
        selectedCoEmpTagsWitness: temp_selectedCoEmpTagsWitness
    });
}



//get sent default raves(All)
loadAllSentRaves() {
    let loginUse = 1;
    //sent raves without particular category (Default)    
    axios.get('http://localhost:8080/api/employee/' + loginUse + '/rave?sent=true&offset=0&limit=10')
        .then(res => {
            this.setState({
                sentRaves: res.data
            })
        });
}

//get received default raves(All)
loadAllReceivedRaves() {
    let loginUse = 1;
    //Received raves without particular category (Default)    
    axios.get('http://localhost:8080/api/employee/' + loginUse + '/rave?sent=false&offset=0&limit=10')
        .then(res => {
            this.setState({
                receivedRaves: res.data
            })
            console.log("after deleted ", res.data)
        });

}


//render raves by category
getRavesByCategory(id, type) {
    let loginUse = 1;

    if (type == 'sent') {

        this.setState({
            sentRaves: []
        });

        axios.get('http://localhost:8080/api/employee/' + loginUse + '/rave?sent=true&category=' + id + '&offset=0&limit=10')
            .then(res => {
                this.setState({
                    sentRaves: res.data
                })
            });
    } else if (type == 'recieved') {

        this.setState({
            receivedRaves: []
        });

        axios.get('http://localhost:8080/api/employee/' + loginUse + '/rave?sent=false&category=' + id + '&offset=0&limit=10')
            .then(res => {
                this.setState({
                    receivedRaves: res.data
                })
            });
    }

}

//count characters of the comment you type
commentCharCounter() {
    let raveCommenter = document.getElementById('raveComment').value;
    let coutRaveComment = raveCommenter.length;
    if (coutRaveComment <= 10) {
        if (coutRaveComment == 10) {
            //"readonly","readonly"
            document.getElementById('raveComment').setAttribute("disabled", "disabled");
        }
        this.setState({
            commentCounter: coutRaveComment
        })


    }
}

//click to remove chaacter when came to limit
commentGetBackspace() {
    document.getElementById('raveComment').removeAttribute("disabled", "disabled");
}


//method for acknowledge the rave
acknowledgeRave = (raveid, loguser) => {
    axios.post("http://localhost:8080/api/rave/" + raveid + "/acknowledgement", { ackBy: loguser })
        .then(res => {
            this.setState({
                acknowledgement: res.data
            })
        });

    this.loadAllReceivedRaves;
}

//method for de acknowledge the rave
deAcknowledgeRave(raveid, ackObj) {
    axios.delete(`http://localhost:8080/api/rave/${raveid}/acknowledgement/${ackObj.ackId}`);

    //render().getReceivedRaves;

    //fetch(`http://localhost:8080/api/rave/${raveid}/acknowledgement/${ackObj.ackId}`,{method:'delete'});
    // //alert("The Data is deleted");
    // console.log("before    ", this.state.receivedRaves);
    this.loadAllReceivedRaves;
    // console.log("after   ", this.state.receivedRaves);

    // let loginUse = 1;
    // //Received raves without particular category (Default)    
    // axios.get('http://localhost:8080/api/employee/' + loginUse + '/rave?sent=false&offset=0&limit=10')
    //     .then(resssa => {
    //         console.log("after deleted ", resssa.data)
    //     });
}


//method for a send a rave 
sendRave() {
    let loguser = 1;

    this.state.selectedCoEmpTags.map(data => {
        const mydata = this.state.sendRaveRequestCoEmps.push(data.id);
        this.setState({
            sendRaveRequestCoEmps: mydata
        })
    });

    this.state.selectedCoEmpTagsWitness.map(data => {
        const mydata = this.state.sendRaveRequestWitness.push(data.id);
        this.setState({
            sendRaveRequestWitness: mydata
        })
    });



    let posterData = {
        coEmployees: this.state.sendRaveRequestCoEmps,
        category: this.state.sendRaveRequestCategory,
        witness: this.state.sendRaveRequestWitness,
        comment: document.getElementById("raveComment").value
    }

    axios.post("http://localhost:8080/api/employee/1/rave", posterData)
        .then(response => {
            this.setState({
                sendRaveResponse: response.data
            })
        });

    this.setState({
        selectedCoEmpTags: [],
        selectedCoEmpTagsWitness: [],
        sendRaveRequestCoEmps: [],
        sendRaveRequestCategory: null,
        sendRaveRequestWitness: [],
        sendRaveRequestComment: null
    });




    document.getElementById("coEmpInputer").innerHTML = "";
    document.getElementById("witnessInputer").innerHTML = "";
    document.getElementById("raveComment").value = "";

}


  render() {




        //get All categories and map to html
        const raveCategoriesItems = this.state.categories.map(items => (
          <div key={items.id}>
              <img src="http://placehold.io/75" /><br />
              <input type="radio" name="raveCate" className="raveCate" value={items.id} onChange={() => this.setCategory(items.id)} />
              <p>
                  {items.displayName}
              </p>
          </div>
      ));



      //Employee suggesions maps to html
      const tookSuggesions = this.state.suggesions.map(data => (
          <li key={data.id} onClick={() => this.addToTags(data)}>
              {data.firstName} {data.lastName} {data.email}
          </li>));

      //slected Employees maps top html
      const getTags = this.state.selectedCoEmpTags.map(data => (
          <li>
              {data.firstName} {data.lastName}<span onClick={() => this.deleteTags(data.id)}> X </span>
          </li>));

      //Employee suggesions for Witness maps to html
      const tookSuggesionsWitness = this.state.suggesionsWitness.map(data => (
